@tailwind base;
@tailwind components;
@tailwind utilities;

/* PAPEM Design System - Identidade Visual Naval Moderna */

@layer base {
  :root {
    /* Base colors - Modern neutral palette */
    --background: 210 30% 98%;
    --foreground: 220 20% 15%;

    --card: 0 0% 100%;
    --card-foreground: 220 20% 15%;

    --popover: 0 0% 100%;
    --popover-foreground: 220 20% 15%;

    /* PAPEM Naval Colors - Exact colors from style guide */
    --primary: 207 100% 29%;        /* #005b94 - Exact PAPEM blue */
    --primary-foreground: 0 0% 100%;
    --primary-hover: 207 100% 24%;  /* #004a7c - Darker PAPEM blue */
    --primary-light: 220 70% 60%;
    --primary-glow: 220 100% 70%;

    --secondary: 210 40% 96%;
    --secondary-foreground: 220 20% 15%;

    --muted: 210 35% 96%;
    --muted-foreground: 220 15% 45%;

    --accent: 350 85% 45%;           /* Bordô/vinho do brasão */
    --accent-foreground: 0 0% 100%;
    --accent-light: 350 60% 85%;

    /* Modern institutional colors */
    --naval-gold: 42 88% 55%;        /* Dourado moderno */
    --naval-burgundy: 350 65% 35%;   /* Bordô refinado */
    --naval-silver: 210 10% 85%;     /* Prata suave */

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --success: 142 76% 36%;
    --success-foreground: 0 0% 100%;
    --success-light: 142 50% 95%;

    --warning: 38 92% 50%;
    --warning-foreground: 0 0% 100%;
    --warning-light: 38 100% 95%;

    --border: 220 20% 90%;
    --input: 220 20% 94%;
    --ring: 220 100% 25%;

    /* Modern Gradients - Updated with PAPEM colors */
    --gradient-primary: linear-gradient(135deg, hsl(207, 100%, 29%) 0%, hsl(207, 100%, 35%) 50%, hsl(207, 100%, 24%) 100%);
    --gradient-secondary: linear-gradient(135deg, hsl(210, 40%, 98%) 0%, hsl(220, 50%, 97%) 100%);
    --gradient-hero: linear-gradient(135deg, hsl(207, 100%, 24%) 0%, hsl(207, 100%, 29%) 100%);
    --gradient-card: linear-gradient(145deg, hsl(0, 0%, 100%) 0%, hsl(210, 25%, 99%) 100%);
    --gradient-accent: linear-gradient(135deg, hsl(350, 85%, 45%) 0%, hsl(350, 85%, 55%) 100%);
    --gradient-naval: linear-gradient(135deg, hsl(207, 100%, 29%) 0%, hsl(207, 100%, 35%) 100%);
    --gradient-glass: linear-gradient(135deg, rgba(255, 255, 255, 0.4) 0%, rgba(255, 255, 255, 0.1) 100%);
    --gradient-subtle: linear-gradient(145deg, hsl(210, 30%, 99%) 0%, hsl(220, 20%, 98%) 100%);

    /* Enhanced Shadows */
    --shadow-sm: 0 1px 3px 0 hsl(220, 50%, 20%, 0.08);
    --shadow-md: 0 4px 12px -2px hsl(220, 50%, 20%, 0.12);
    --shadow-lg: 0 10px 25px -3px hsl(220, 50%, 20%, 0.15);
    --shadow-xl: 0 20px 40px -4px hsl(220, 50%, 20%, 0.20);
    --shadow-2xl: 0 25px 50px -12px hsl(220, 50%, 20%, 0.25);

    /* Animation Variables */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);

    --radius: 1rem;

    --sidebar-background: 0 0% 98%;
    --sidebar-foreground: 240 5.3% 26.1%;
    --sidebar-primary: 240 5.9% 10%;
    --sidebar-primary-foreground: 0 0% 98%;
    --sidebar-accent: 240 4.8% 95.9%;
    --sidebar-accent-foreground: 240 5.9% 10%;
    --sidebar-border: 220 13% 91%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;

    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;

    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;

    --primary: 210 40% 98%;
    --primary-foreground: 222.2 47.4% 11.2%;

    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;

    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

/* Modern UI Components */
@layer components {
  /* Survey Cards */
  .survey-card {
    @apply bg-gradient-card border border-border shadow-md hover:shadow-lg transition-all duration-300;
    backdrop-filter: blur(10px);
  }

  .survey-card-enhanced {
    @apply bg-gradient-card border border-border shadow-lg hover:shadow-xl transition-all duration-300;
    background: var(--gradient-subtle);
    backdrop-filter: blur(10px);
  }

  /* Modern Buttons */
  .modern-button {
    @apply font-medium rounded-xl px-6 py-3 transition-all duration-300 shadow-sm hover:shadow-md;
    background: var(--gradient-primary);
  }

  .modern-button-outline {
    @apply font-medium rounded-xl px-6 py-3 border-2 border-primary/20 bg-white/80 hover:bg-primary/5 transition-all duration-300;
    backdrop-filter: blur(10px);
  }

  /* Question Cards - Following PAPEM Style Guide */
  .question-card {
    @apply bg-gradient-to-br from-white/80 to-white/60 border border-slate-200/70 backdrop-blur-sm;
    @apply rounded-2xl shadow-lg transition-all duration-300 hover:shadow-xl;
    @apply animate-fade-in;
  }

  .question-card-error {
    @apply border-destructive/50 shadow-destructive/10;
  }

  /* Option Button Styles - Following PAPEM Style Guide */
  .option-button {
    @apply px-5 py-4 rounded-xl text-sm font-semibold transition-all duration-200 cursor-pointer;
    @apply flex items-center justify-center;
    @apply hover:scale-[1.02] active:scale-[0.98];
    animation: slide-up 0.4s ease-out forwards;
    opacity: 0;
    transform: translateY(20px);
  }

  .option-button-selected {
    @apply bg-[#005b94] text-white border-2 border-[#004a7c] shadow-md;
    @apply hover:bg-[#004a7c] hover:shadow-lg;
  }

  .option-button-unselected {
    @apply bg-slate-100 text-slate-700 border-2 border-slate-200;
    @apply hover:bg-slate-200 hover:border-slate-300;
  }

  /* Progress Elements */
  .progress-circle {
    @apply w-10 h-10 rounded-full border-2 flex items-center justify-center font-bold transition-all duration-300;
  }

  .progress-circle-active {
    @apply text-white shadow-lg scale-110;
    background: var(--gradient-primary);
    border-color: hsl(var(--primary));
  }

  .progress-circle-completed {
    @apply bg-success text-white border-success shadow-md;
  }

  .progress-circle-inactive {
    @apply bg-white border-border text-muted-foreground;
  }

  /* Naval Decorative Elements */
  .naval-accent {
    position: relative;
  }

  .naval-accent::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: var(--gradient-primary);
    border-radius: 0 0 8px 8px;
  }

  /* Subtle animations */
  .fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  .slide-up {
    animation: slideUp 0.5s ease-out;
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes slideUp {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  html {
    scroll-behavior: smooth;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-muted/30;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-primary/50 rounded-full;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-primary/70;
  }
}

src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

